mapper.select:
SELECT exam_subject_sequence_no, subject 
FROM jwland.exam_subject
ORDER BY exam_subject_sequence_no;

mapper.delete:
DELETE FROM jwland.exam_master
WHERE exam_type_sequence_no = #{examTypeSequenceNo}
AND exam_subject_sequence_no =  #{examSubjectSequenceNo}
AND account_sequence_no =  #{accountSequenceNo}
